package pages;import org.openqa.selenium.WebElement;import org.openqa.selenium.support.FindBy;public class UspsPrice extends Page {    @FindBy(xpath = "//input[@id='quantity-0']")    private WebElement quantity;    @FindBy(xpath = "//input[@value='Calculate']")    private WebElement calculate;    @FindBy(xpath = "//div[@id='total']")    private WebElement total;    public void defineQuantity(String el) {        quantity.sendKeys(el);    }    public void getCalculate() {        click(calculate);    }    public String getTotal() {        return total.getText();    }}